mapper.select:
SELECT *,Convert(decimal(18,2),pitch_use/weight*100) baifenbi FROM quality_by_plate_real_time_prodocution_data qba
qba.production_time > #{beginTime}
and qba.production_time < #{endTime}
order by qba.production_time asc

SELECT top 1 ISNULL(Convert(decimal(18,2),materials_use_1/weight*100), 0) baifenbi1, ISNULL(Convert(decimal(18,2),materials_use_2/weight*100), 0) baifenbi2, ISNULL(Convert(decimal(18,2),materials_use_3/weight*100), 0) baifenbi3, ISNULL(Convert(decimal(18,2),materials_use_4/weight*100), 0) baifenbi4, ISNULL(Convert(decimal(18,2),materials_use_5/weight*100), 0) baifenbi5, ISNULL(Convert(decimal(18,2),materials_use_6/weight*100), 0) baifenbi6, ISNULL(Convert(decimal(18,2),pitch_use/weight*100), 0) baifenbi_liqing, ISNULL(Convert(decimal(18,2),bone_flour_use/weight*100), 0) baifenbi_fen FROM quality_by_plate_real_time_prodocution_data qba
convert(varchar,qba.production_time ,120) like #{time}
order by qba.production_time desc
